sp_widget.client_script:
function($scope,$rootScope) {
		/* widget controller */
		var c = this;
	c.updateList = function(title){
		c.server.get({
			action: "updateNotes",
			title: c.data.title,
			note: c.data.note,
			noteID: c.data.noteID
		}).then(function(){
			$rootScope.$emit("listUpdated");
		});
	}

		$rootScope.$on('noteClicked', function(event,data) {
			//console.log($rootScope.noteID);
			c.server.get({
				action: 'getNote',
				noteID: $rootScope.noteID
			}).then(function(r) {
				c.data.title = r.data.note.title;
				c.data.note = r.data.note.note;
				c.data.noteID = r.data.note.sys_id;
			});
		});

	}

sp_widget.script:
(function() {
		/* populate the 'data' object */
	data.note = {};

	if(input && input.action == "updateNotes"){
		var noteGR = new GlideRecord("x_snc_createnotes_note");
		var noteID = input.noteID;	
		noteGR.get(noteID);
		noteGR.title = input.title;
		noteGR.note = input.note;
		noteGR.update();
	}

	if(input && input.noteID){
		if(input.action == 'getNote'){
			var noteID = input.noteID;
			gs.addInfoMessage(noteID);
			var noteGR = new GlideRecord("x_snc_createnotes_note");
			noteGR.get(noteID);
			$sp.getRecordValues(data.note,noteGR,"title,note,sys_id");
		}
	}
})();
